package com.ust.day3;

public class String_Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "program";
		
		System.out.println(str1.substring(0,7));	// from 1st to 7th character
		
		System.out.println(str1.substring(0,5));	// from 1st to 5th
		
		System.out.println(str1.substring(3,5));
	}

}
